      {/* Add the admin registration tab */}
      {activeTab === 'admin-register' && (
        <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-xl font-bold mb-4">Register New Admin</h2>
        {error && (
          <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-md">
          {error}
          </div>
        )}
        <form onSubmit={handleRegister}>
        <div className="mb-4">
        <label className="block text-gray-700 text-sm font-medium mb-2">
        Username
        </label>
        <input
        type="text"
        value={registerForm.username}
        onChange={(e) => setRegisterForm({...registerForm, username: e.target.value})}
        className="w-full p-2 border rounded-md"
        required
        />
        </div>
        
        <div className="mb-4">
        <label className="block text-gray-700 text-sm font-medium mb-2">
        Password
        </label>
        <input
        type="password"
        value={registerForm.password}
        onChange={(e) => setRegisterForm({...registerForm, password: e.target.value})}
        className="w-full p-2 border rounded-md"
        required
        />
        </div>
        
        <div className="mb-4">
        <label className="block text-gray-700 text-sm font-medium mb-2">
        Confirm Password
        </label>
        <input
        type="password"
        value={registerForm.confirmPassword}
        onChange={(e) => setRegisterForm({...registerForm, confirmPassword: e.target.value})}
        className="w-full p-2 border rounded-md"
        required
        />
        </div>
        
        <div className="mb-4">
        <label className="block text-gray-700 text-sm font-medium mb-2">
        Admin Level
        </label>
        <select
        value={registerForm.userLevel}
        onChange={(e) => setRegisterForm({...registerForm, userLevel: parseInt(e.target.value)})}
        className="w-full p-2 border rounded-md"
        >
        <option value={1}>Super Admin</option>
        <option value={2}>Admin</option>
        </select>
        </div>
        
        <button
        type="submit"
        disabled={loading}
        className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:bg-gray-400"
        >
        {loading ? 'Registering...' : 'Register Admin'}
        </button>
        </form>
        </div>
      )}
      </div>
      </div>
    );
  };
  
  export default AdminDashboard;
