import React, { useState, useEffect } from 'react';
import { BarChart3, History, Download, Building, Lock, UserPlus, LogOut } from 'lucide-react';

const AdminDashboard = () => {
  const [activeTab, setActiveTab] = useState('login');
  const [departments, setDepartments] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [newDepartment, setNewDepartment] = useState('');
  const [newQuestion, setNewQuestion] = useState('');
  const [selectedDepartment, setSelectedDepartment] = useState('');
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [historyData, setHistoryData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [selectedDepartmentForHistory, setSelectedDepartmentForHistory] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [exportLoading, setExportLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userLevel, setUserLevel] = useState(2); // 1=superadmin, 2=admin
  const [username, setUsername] = useState('');
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [registerForm, setRegisterForm] = useState({ 
    username: '', 
    password: '', 
    confirmPassword: '',
    userLevel: 2 
  });
  
  const API_BASE_URL = 'http://localhost:5000/api';
  
  // Check if user is already logged in
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      verifyToken(token);
    }
  }, []);
  
  const verifyToken = async (token) => {
    try {
      const response = await fetch(`${API_BASE_URL}/admin/me`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setIsLoggedIn(true);
        setUserLevel(data.userLevel);
        setUsername(data.username);
        setActiveTab(data.userLevel === 1 ? 'departments' : 'questions');
      } else {
        localStorage.removeItem('token');
      }
    } catch (err) {
      localStorage.removeItem('token');
      console.error('Token verification failed:', err);
    }
  };
  
  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    try {
      const response = await fetch(`${API_BASE_URL}/admin/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(loginForm)
      });
      
      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('token', data.token);
        setIsLoggedIn(true);
        setUserLevel(data.userLevel);
        setUsername(data.username);
        setActiveTab(data.userLevel === 1 ? 'departments' : 'questions');
      } else {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Login failed');
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };
  
  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    if (registerForm.password !== registerForm.confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }
    
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_BASE_URL}/admin/register`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          username: registerForm.username,
          password: registerForm.password,
          userLevel: registerForm.userLevel
        })
      });
      
      if (response.ok) {
        setRegisterForm({ username: '', password: '', confirmPassword: '', userLevel: 2 });
        setError('');
        setActiveTab('login');
      } else {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Registration failed');
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };
  
  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setUserLevel(2);
    setUsername('');
    setActiveTab('login');
  };
  
  // ... (keep all your existing functions like loadDepartments, loadQuestions, etc.) ...
  // Load departments
  const loadDepartments = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/departments`);
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      setDepartments(Array.isArray(data) ? data : []);
      setError('');
    } catch (err) {
      console.error('Failed to load departments:', err);
      setDepartments([]);
      setError('Failed to load departments: ' + err.message);
    } finally {
      setLoading(false);
    }
  };
  
  // Load questions
  // Load questions
  const loadQuestions = async (departmentId = '') => {
    try {
      setLoading(true);
      let url = `${API_BASE_URL}/questions`;
      
      if (departmentId) {
        const dept = departments.find(d => d.DepartmentID == departmentId);
        if (dept) {
          url = `${API_BASE_URL}/departments/${dept.URLSlug}/questions`;
        }
      }
      
      const response = await fetch(url, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      setQuestions(Array.isArray(data) ? data : []);
      setError('');
    } catch (err) {
      console.error('Failed to load questions:', err);
      setQuestions([]);
      setError('Failed to load questions: ' + err.message);
    } finally {
      setLoading(false);
    }
  };
  
  // Load history data
  const loadHistoryData = async (departmentId = '', startDate = '', endDate = '') => {
    try {
      setLoading(true);
      let url = `${API_BASE_URL}/reports/history`;
      const params = new URLSearchParams();
      
      if (departmentId) params.append('departmentId', departmentId);
      if (startDate) params.append('startDate', startDate);
      if (endDate) params.append('endDate', endDate);
      
      if (params.toString()) {
        url += `?${params.toString()}`;
      }
      
      const response = await fetch(url);
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      setHistoryData(Array.isArray(data) ? data : []);
      setFilteredData(Array.isArray(data) ? data : []);
      setError('');
    } catch (err) {
      console.error('Failed to load history data:', err);
      setHistoryData([]);
      setFilteredData([]);
      setError('Failed to load history data: ' + err.message);
    } finally {
      setLoading(false);
    }
  };
  
  // Export to Excel function
  const exportToExcel = async () => {
    try {
      setExportLoading(true);
      
      let url = `${API_BASE_URL}/reports/export`;
      const params = new URLSearchParams();
      
      if (selectedDepartmentForHistory) params.append('departmentId', selectedDepartmentForHistory);
      if (startDate) params.append('startDate', startDate);
      if (endDate) params.append('endDate', endDate);
      
      if (params.toString()) {
        url += `?${params.toString()}`;
      }
      
      const response = await fetch(url);
      
      if (!response.ok) {
        throw new Error(`Export failed: ${response.status}`);
      }
      
      // Get the blob from response
      const blob = await response.blob();
      
      // Create download link
      const downloadUrl = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = downloadUrl;
      
      // Generate filename with current date
      const now = new Date();
      const dateStr = now.toISOString().split('T')[0];
      const deptName = selectedDepartmentForHistory ? 
      departments.find(d => d.DepartmentID = selectedDepartmentForHistory)?.Name || 'Selected' : 'All';
      
      link.download = `Survey_Report_${deptName}_${dateStr}.xlsx`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      
      // Clean up
      window.URL.revokeObjectURL(downloadUrl);
      
    } catch (err) {
      console.error('Export failed:', err);
      setError('Export failed: ' + err.message);
    } finally {
      setExportLoading(false);
    }
  };
  
  // Handle filter changes
  const handleFilterChange = () => {
    loadHistoryData(selectedDepartmentForHistory, startDate, endDate);
  };
  
  // Load reports - Fixed to handle the actual API response
  // Updated loadReports function to fetch only active questions
const loadReports = async () => {
  setLoading(true);
  try {
    // Fetch only active questions with their responses
    const response = await fetch('/api/reports/active-surveys', {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    setReports(data);
  } catch (error) {
    console.error('Error loading reports:', error);
    setError('Failed to load reports');
  } finally {
    setLoading(false);
  }
};

// Alternative approach if you need to modify the existing API call
const loadReportsAlternative = async () => {
  setLoading(true);
  try {
    // Fetch reports with active questions filter
    const response = await fetch('/api/reports?active_only=true', {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    
    // Filter for active questions on the frontend if needed
    const activeReports = data.filter(report => report.isActive === true);
    setReports(activeReports);
  } catch (error) {
    console.error('Error loading reports:', error);
    setError('Failed to load reports');
  } finally {
    setLoading(false);
  }
};
  // Add department
  const addDepartment = async () => {
    if (!newDepartment.trim()) {
      setError('Department name is required');
      return;
    }
    
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/departments`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: newDepartment.trim() })
      });
      
      if (response.ok) {
        setNewDepartment('');
        await loadDepartments();
      } else {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to add department');
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };
  
  // Add question
  // Add question
  const addQuestion = async () => {
    if (!selectedDepartment || !newQuestion.trim()) {
      setError('Department and question text are required');
      return;
    }
    
    setLoading(true);
    try {
      const dept = departments.find(d => d.DepartmentID == selectedDepartment);
      if (!dept) {
        throw new Error('Department not found');
      }
      
      const response = await fetch(`${API_BASE_URL}/departments/${dept.URLSlug}/questions`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ questionText: newQuestion.trim() })
      });
      
      if (response.ok) {
        setNewQuestion('');
        setSelectedDepartment('');
        await loadQuestions();
      } else {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to add question');
      }
